import 'dart:convert';
import 'package:http/http.dart' as http;

class GitHubService {
  final String accessToken;

  GitHubService(this.accessToken);

  // GitHub پر نیا repository بنانا
  Future<String> createRepository(String repoName, String code) async {
    try {
      final response = await http.post(
        Uri.parse('https://api.github.com/user/repos'),
        headers: {
          'Authorization': 'token $accessToken',
          'Accept': 'application/vnd.github.v3+json',
        },
        body: jsonEncode({
          'name': repoName,
          'description': 'Auto-generated by Aladdin App',
          'private': false,
          'auto_init': true,
        }),
      );

      if (response.statusCode == 201) {
        final repoData = jsonDecode(response.body);
        final repoUrl = repoData['html_url'];
        
        // main.dart فائل upload کریں
        await _uploadFile(repoName, 'lib/main.dart', code);
        
        return repoUrl;
      } else {
        throw Exception('GitHub Error: ${response.statusCode}');
      }
    } catch (e) {
      throw Exception('GitHub connection failed: $e');
    }
  }

  Future<void> _uploadFile(String repoName, String filePath, String content) async {
    final response = await http.put(
      Uri.parse('https://api.github.com/repos/$repoName/contents/$filePath'),
      headers: {
        'Authorization': 'token $accessToken',
        'Accept': 'application/vnd.github.v3+json',
      },
      body: jsonEncode({
        'message': 'Add generated Flutter app',
        'content': base64Encode(utf8.encode(content)),
      }),
    );

    if (response.statusCode != 201) {
      throw Exception('File upload failed: ${response.statusCode}');
    }
  }
}
